mapper.select:
SELECT B.RECORD_ID AS recordId, B.FILE_ID AS fileId,F.TITLE AS title,F.FILE_CODE AS fileCode,F.SUBJECT AS subject,F.FILE_TYPE AS fileType,F.SECRET_LEVEL AS secretLevel,
B.BEGIN_TIME AS beginTime,B.END_TIME AS endTime,B.APPROVAL_STATUS AS approvalStatus,B.APPROVAL_USER AS approvalUser,B.APPROVAL_TIME AS approvalTime,B.REMARK AS remark
FROM ARCHIVES_BORROW_FILE B LEFT JOIN ARCHIVES_FILE F ON B.ORG_ID=F.ORG_ID AND B.FILE_ID=F.FILE_ID
WHERE B.ORG_ID=#{orgId}

	AND B.APPROVAL_STATUS = #{approvalStatus}


	AND B.CREATE_USER=#{accountId}
