mapper.select:
SELECT D.RECORD_ID AS recordId,D.REMARK AS remark,D.CREATE_TIME AS createTime,D.CREATE_USER AS createUser,F.FILE_ID AS fileId,F.FILE_CODE AS fileCode,
F.SUBJECT AS subject,F.TITLE AS title,F.SECRET_LEVEL AS secretLevel,
(SELECT U.USER_NAME FROM USER_INFO U WHERE U.ORG_ID=#{orgId} AND U.ACCOUNT_ID=D.CREATE_USER) AS createUserName
FROM
ARCHIVES_DESTROY_RECORD D LEFT JOIN ARCHIVES_FILE F ON D.ORG_ID=F.ORG_ID AND D.ARCHIVES_ID=F.FILE_ID
WHERE 
D.ORG_ID=#{orgId}
AND D.ARCHIVES_TYPE='1'

	AND D.CREATE_USER = #{accountId}


	AND D.CREATE_TIME >=#{beginTime}


	AND D.CREATE_TIME <=#{endTime}


	AND (F.TITLE LIKE #{search} OR F.SUBJECT LIKE #{search} OR F.SUBHEADING LIKE #{search} OR F.FILE_CODE LIKE #{search})

SELECT D.RECORD_ID AS recordId,D.REMARK AS remark,D.CREATE_TIME AS createTime,D.CREATE_USER AS createUser,V.VOLUME_ID AS volumeId,V.VOLUME_CODE AS volumeCode,
V.VOLUME_TITLE AS volumeTitle,V.SECRET_LEVEL AS secretLevel,
(SELECT U.USER_NAME FROM USER_INFO U WHERE U.ORG_ID=#{orgId} AND U.ACCOUNT_ID=D.CREATE_USER) AS createUserName
FROM
ARCHIVES_DESTROY_RECORD D LEFT JOIN ARCHIVES_VOLUME V ON D.ORG_ID=V.ORG_ID AND D.ARCHIVES_ID=V.VOLUME_ID
WHERE 
D.ORG_ID=#{orgId}
AND D.ARCHIVES_TYPE='2'

	AND D.CREATE_USER = #{accountId}


	AND D.CREATE_TIME >=#{beginTime}


	AND D.CREATE_TIME <=#{endTime}


	AND (V.VOLUME_TITLE LIKE #{search} OR V.VOLUME_CODE LIKE #{search})
