mapper.select:
SELECT F.FILE_ID AS fileId, V.VOLUME_TITLE AS volumeTitle,F.TITLE AS title,F.SUBJECT AS subject,F.FILE_TYPE AS fileType,F.PAGE_TOTAL AS pageTotal,F.SECRET_LEVEL AS secretLevel
FROM
ARCHIVES_FILE F LEFT JOIN ARCHIVES_VOLUME V ON F.ORG_ID=V.ORG_ID AND F.VOLUME_ID=V.VOLUME_ID
WHERE F.ORG_ID=#{orgId} AND F.DESTORY_FLAG!='1'

	AND V.MANAGE_USER = #{accountId}


	AND F.VOLUME_ID = #{volumeId}


	AND F.FILE_TYPE = #{fileType}


	AND F.SECRET_LEVEL = #{secretLevel}


	AND (F.TITLE LIKE #{search} OR F.SUBJECT LIKE #{search} OR F.SUBHEADING LIKE #{search} OR F.FILE_CODE LIKE #{search})

SELECT F.FILE_ID AS fileId, V.VOLUME_TITLE AS volumeTitle,F.TITLE AS title,F.SUBJECT AS subject,F.FILE_TYPE AS fileType,
F.PAGE_TOTAL AS pageTotal,F.SECRET_LEVEL AS secretLevel,F.SEND_ORG AS sendOrg
FROM
ARCHIVES_FILE F LEFT JOIN ARCHIVES_VOLUME V ON F.ORG_ID=V.ORG_ID AND F.VOLUME_ID=V.VOLUME_ID
WHERE F.ORG_ID=#{orgId} AND F.DESTORY_FLAG!='1'

	AND V.VOLUME_ID IN (SELECT TEMP.VOLUME_ID FROM ARCHIVES_VOLUME TEMP WHERE TEMP.ORG_ID=#{orgId} AND TEMP.REPOSITORY_ID=#{repositoryId})


	AND F.VOLUME_ID = #{volumeId}


	AND F.FILE_TYPE = #{fileType}


	AND F.SECRET_LEVEL = #{secretLevel}


	AND (F.TITLE LIKE #{search} OR F.SUBJECT LIKE #{search} OR F.SUBHEADING LIKE #{search} OR F.FILE_CODE LIKE #{search} OR F.SEND_ORG LIKE #{search})
