mapper.select:
SELECT * FROM
CALENDAR
WHERE ORG_ID=#{orgId}
AND END <=#{nowTime}
AND STATUS='0'

SELECT ID AS id, CALENDAR_ID AS calendarId, START AS start, END AS end,SHARE AS share,
CONTENT AS title, ACCOUNT_ID AS accountId, TYPE AS type, STATUS AS status, ADVANCE AS advance, MSG_TYPE AS msgType,URL AS url
FROM CALENDAR
WHERE 
ORG_ID=#{orgId}
AND ACCOUNT_ID=#{accountId}
AND END>=#{nowDate}

 SELECT ID AS id, CALENDAR_ID AS calendarId, `START` AS calendarStart, `END` AS calendarEnd, CONTENT AS title, SHARE AS share,
 ACCOUNT_ID AS accountId, TYPE AS type, STATUS AS status, ADVANCE AS advance, MSG_TYPE AS msgType,URL AS url,CREATE_TIME AS createTime
 FROM CALENDAR
 WHERE 
 ORG_ID=#{orgId}
 AND ACCOUNT_ID=#{accountId}

	AND TYPE=#{type}


	AND START >= #{beginTime}


	AND START <= #{endTime}


	AND CONTENT LIKE #{search}

 SELECT ID AS id, CALENDAR_ID AS calendarId, `START` AS calendarStart, `END` AS calendarEnd, CONTENT AS title,
 TYPE AS type, STATUS AS status,URL AS url, CREATE_USER AS createUser,(SELECT U.USER_NAME FROM USER_INFO U WHERE U.ORG_ID=#{orgId} AND U.ACCOUNT_ID=CREATE_USER) AS createUserName
 FROM CALENDAR
 WHERE 
 ORG_ID=#{orgId}
 AND ACCOUNT_ID=#{accountId}

	AND START >= #{beginTime}


	AND START <= #{endTime}

	ORDER BY `START` DESC LIMIT 0,5
