SELECT ID AS id,DIARY_DAY AS diaryDay,DIARY_ID AS diaryId,TITLE AS title,CREATE_TIME AS createTime,CREATE_USER AS createUser,
ATTACH AS attach,DIARY_TYPE AS diaryType,CONTENT AS content
FROM DIARY
WHERE ORG_ID = #{orgId}
AND CREATE_USER = #{accountId}

	AND TITLE LIKE #{search}


	AND DIARY_DAY = #{diaryDay}

SELECT ID AS id,DIARY_DAY AS diaryDay,DIARY_ID AS diaryId,TITLE AS title,CREATE_TIME AS createTime,CREATE_USER AS createUser,
ATTACH AS attach,DIARY_TYPE AS diaryType,CONTENT AS content
FROM DIARY
WHERE ORG_ID = #{orgId}
AND CREATE_USER != #{accountId}

	AND DIARY_DAY >= #{beginTime}


	AND DIARY_DAY <=#{endTime}

AND(
FUN_INTE_ARRAY(USER_PRIV,#{accountId}) > 0

	OR FUN_INTE_ARRAY(DEPT_PRIV,#{deptId})> 0


	OR FUN_INTE_ARRAY(LEAVE_PRIV,#{leaveId}) > 0

OR USER_PRIV = '@all'
OR DEPT_PRIV = '@all'
OR LEAVE_PRIV = '@all'
)

	AND TITLE LIKE #{search}

SELECT D.ID AS id,D.DIARY_ID AS diaryId,D.TITLE AS title,D.CREATE_TIME AS createTime,D.ATTACH AS attach,D.DIARY_TYPE AS diaryType,
(SELECT U.USER_NAME FROM USER_INFO U WHERE U.ORG_ID=#{orgId} AND U.ACCOUNT_ID = D.CREATE_USER) AS createUserName,DIARY_DAY AS diaryDay
FROM DIARY D
WHERE D.ORG_ID = #{orgId}

	AND D.CREATE_USER IN

		#{item}



	AND D.CREATE_TIME >=#{beginTime}


	AND D.CREATE_TIME <=#{endTime}


	AND D.TITLE LIKE #{search}

SELECT ID AS id,DIARY_DAY AS diaryDay,DIARY_ID AS diaryId,TITLE AS title,CREATE_TIME AS createTime,CREATE_USER AS createUser,
ATTACH AS attach,DIARY_TYPE AS diaryType,CONTENT AS content,DIARY_TYPE AS diaryType,ATTACH_PRIV AS attachPriv
FROM DIARY
WHERE ORG_ID = #{orgId}
AND CREATE_USER = #{accountId}

	AND TITLE LIKE #{search}


	AND CREATE_TIME >=#{beginTime}


	AND CREATE_TIME <=#{endTime}

SELECT COUNT(1) AS total 
FROM 
DIARY 
WHERE
ORG_ID=#{orgId}
AND CREATE_USER=#{accountId}
