mapper.select:
SELECT NEWS_ID AS newsId,NEWS_TITLE AS newsTitle,NEWS_TYPE AS
newsType,CREATE_TIME AS createTime,DEL_FLAG AS delFlag,SUBHEADING AS subheading,
(SELECT U.USER_NAME FROM USER_INFO U WHERE U.ORG_ID=ORG_ID AND
U.ACCOUNT_ID=CREATE_USER) AS createUser,END_TIME AS endTime,ONCLICK_COUNT AS onclickCount
FROM NEWS
WHERE
ORG_ID=#{orgId}
AND STATUS = '0'

	AND CREATE_USER=#{accountId}


	AND NEWS_TITLE LIKE#{search}

SELECT NEWS_ID AS newsId,NEWS_TITLE AS newsTitle,NEWS_TYPE AS newsType,CREATE_TIME AS createTime,SUBHEADING AS subheading, SEND_TIME AS sendTime,ONCLICK_COUNT AS onclickCount, (SELECT U.USER_NAME FROM USER_INFO U WHERE U.ORG_ID=ORG_ID AND U.ACCOUNT_ID=CREATE_USER) AS createUser,CASE WHEN FUN_INTE_ARRAY(READER,#{accountId}) = 0 then 'true' else 'false' END AS readStatus FROM NEWS WHERE ORG_ID=#{orgId} AND DEL_FLAG = 0 AND STATUS = '0'
AND NEWS_TYPE = #{newsType}
AND FUN_INTE_ARRAY(READER,#{accountId}) > 0
AND FUN_INTE_ARRAY(READER,#{accountId}) = 0
AND SEND_TIME >= #{beginTime}
AND SEND_TIME <=#{endTime}
AND NEWS_TITLE LIKE #{search}
AND( FUN_INTE_ARRAY(USER_PRIV,#{accountId}) > 0
OR FUN_INTE_ARRAY(DEPT_PRIV,#{deptId})> 0
OR FUN_INTE_ARRAY(LEVEL_PRIV,#{levelId}) > 0
OR USER_PRIV = '@all' OR DEPT_PRIV = '@all' OR LEVEL_PRIV = '@all' )
