SELECT NOTICE_ID AS noticeId,NOTICE_TITLE AS noticeTitle,NOTICE_TYPE
AS noticeType,CREATE_TIME AS createTime,STATUS AS status,
(SELECT
U.USER_NAME FROM USER_INFO U WHERE U.ORG_ID=ORG_ID AND
U.ACCOUNT_ID=CREATE_USER) AS createUser,SEND_TIME AS sendTime,END_TIME
AS endTime,
APPROVAL_STATUS AS approvalStatus
FROM NOTICE
WHERE
ORG_ID=#{orgId}

	AND CREATE_USER=#{accountId}


	AND NOTICE_TITLE LIKE#{search}


	AND NOTICE_TYPE = #{noticeType}


	AND SEND_TIME >= #{beginTime}


	AND SEND_TIME <=#{endTime}

SELECT NOTICE_ID AS noticeId,NOTICE_TITLE AS noticeTitle,NOTICE_TYPE AS noticeType,CREATE_TIME AS createTime,STATUS AS status, (SELECT U.USER_NAME FROM USER_INFO U WHERE U.ORG_ID=ORG_ID AND U.ACCOUNT_ID=CREATE_USER) AS createUser, SEND_TIME AS sendTime,END_TIME AS endTime, APPROVAL_STATUS AS approvalStatus,CASE WHEN FUN_INTE_ARRAY(READER,#{accountId}) = 0 then 'true' else 'false' END AS readStatus FROM NOTICE WHERE ORG_ID=#{orgId} AND STATUS = '1' AND( FUN_INTE_ARRAY(USER_PRIV,#{accountId}) > 0
OR FUN_INTE_ARRAY(DEPT_PRIV,#{deptId}) > 0
OR FUN_INTE_ARRAY(LEAVE_PRIV,#{leaveId}) > 0
OR USER_PRIV = '@all' OR DEPT_PRIV = '@all' OR LEAVE_PRIV = '@all' )
AND NOTICE_TYPE = #{noticeType}
AND NEWS_TITLE LIKE#{search}
AND SEND_TIME >=#{beginTime}
AND SEND_TIME <=#{endTime}
AND FUN_INTE_ARRAY(READER,#{accountId}) > 0
AND FUN_INTE_ARRAY(READER,#{accountId}) <= 0

SELECT COUNT(1) AS ZS
FROM NOTICE
WHERE ORG_ID=#{orgId}
AND NOTICE_ID=#{noticeId}
AND
STATUS=='0'
AND END_TIME <=#{endTime}
