mapper.select:
SELECT  (@i:=@i+1) AS num, D.DETAIL_ID AS detailId,D.INQUIRY_ID AS inquiryId,D.PRODUCT_ID AS productId,D.COUNT AS count,D.DELIVERY AS delivery,D.REMARK AS remark,
(SELECT B.MATERIEL_CODE FROM ERP_BOM B WHERE B.ORG_ID=#{orgId} AND P.BOM_ID=B.BOM_ID) AS materielCode,P.PRODUCT_NAME AS productName,P.MODEL AS model,
(SELECT U.CN_NAME FROM ERP_UNIT U WHERE U.ORG_ID=#{orgId} AND U.TYPE=P.UNIT) AS unit
FROM CRM_INQUIRY_DETAIL D LEFT JOIN ERP_PRODUCT P ON D.ORG_ID=P.ORG_ID AND D.PRODUCT_ID=P.PRODUCT_ID,(SELECT @i := -1) AS it
WHERE D.ORG_ID=#{orgId}
AND D.INQUIRY_ID=#{inquiryId}
