mapper.select:
        SELECT A.BOM_DETAIL_ID AS bomDetailId,A.MATERIEL_CODE AS materielCode,A.PARAM AS param,B.MATERIEL_NAME AS materielName,B.TYPE AS type,A.USE_COUNT AS useCount,B.UNIT AS unit,A.CREATE_TIME as createTime,A.REMARK AS remark 
        FROM ERP_BOM_DETAIL A LEFT JOIN ERP_MATERIEL B ON A.MATERIEL_CODE=B.MATERIEL_CODE AND A.ORG_ID=B.ORG_ID
		WHERE A.BOM_ID=#{bomId} AND A.BOM_DETAIL_ID = #{bomDetailId} AND A.ORG_ID=#{orgId}

        SELECT A.BOM_DETAIL_ID AS bomDetailId,A.BOM_ID AS bomId,B.BOM_NAME AS bomName,B.VERSION AS version,B.MAP_NUMBER AS mapNumber,B.PRODUCT_NUMBER AS productNumber,A.USE_COUNT AS useCount,A.REMARK AS remark 
        FROM ERP_BOM_DETAIL A LEFT JOIN ERP_BOM B ON A.CHILD_BOM_ID=B.BOM_ID AND A.ORG_ID=B.ORG_ID
		WHERE A.BOM_ID=#{bomId} AND B.BOM_NAME LIKE #{bomName} AND A.ORG_ID=#{orgId}
