mapper.select:
SELECT O.DETAIL_ID AS detailId,P.PRODUCT_NAME AS productName,P.MODEL AS model,(SELECT TMP.BOM_NAME FROM ERP_BOM TMP WHERE TMP.BOM_ID=P.BOM_ID AND TMP.ORG_ID=P.ORG_ID) AS bomId,O.COUNT AS count,P.UNIT AS unit,O.REMARK as remark 
FROM ERP_PRODUCT P LEFT JOIN ERP_ORDER_DETAIL O ON  O.PRODUCT_ID=P.PRODUCT_ID
WHERE O.ORDER_ID=#{orderId} 

     AND (P.PRODUCT_NAME LIKE #{search} OR P.MODEL LIKE #{search})

AND O.ORG_ID=#{orgId}
