mapper.select:
SELECT ID, PRODUCT_ID, MODEL, SORT_NO, PRODUCT_NAME, UNIT, SORT_LEAVE, PRODUCT_IMG, PARAM,(SELECT TMP.BOM_NAME FROM ERP_BOM TMP WHERE TMP.BOM_ID=P.BOM_ID AND TMP.ORG_ID=P.ORG_ID) AS BOM_ID, REMARK, CREATE_USER, CREATE_TIME,ORG_ID 
FROM ERP_PRODUCT P 
WHERE PRODUCT_NAME LIKE #{productName} AND ORG_ID=#{orgId}

SELECT P.PRODUCT_NAME AS productName,P.MODEL AS model,P.PARAM AS param,P.REMARK AS remark,P.PRODUCT_IMG AS productImg,
B.BOM_ID AS bomId,B.BOM_NAME AS bomName,B.VERSION AS version,B.PRODUCT_NUMBER AS productNumber,B.MAP_NUMBER AS mapNumber,B.STATUS AS status
FROM ERP_PRODUCT P JOIN ERP_BOM B ON P.BOM_ID=B.BOM_ID AND P.PRODUCT_ID=#{productId} AND P.ORG_ID=#{orgId}
