SELECT F.ASSETS_ID AS assetsId,F.ASSETS_NAME AS assetsName,F.OWN_DEPT AS ownDept,F.BRAND AS brand,F.MODEL AS model,
F.ASSETS_CODE AS assetsCode,F.PURCHASE_PRICE AS purchasePrice,F.CREATE_TIME AS createTime,F.SORT_ID AS sortId,
(SELECT S.SORT_NAME FROM FIXED_ASSETS_SORT S WHERE S.ORG_ID=#{orgId} AND F.SORT_ID=S.SORT_ID) AS sortName,
(SELECT D.DEPT_NAME FROM UNIT_DEPT D WHERE D.ORG_ID=#{orgId} AND D.DEPT_ID=F.OWN_DEPT) AS ownDeptName,F.STATUS AS status 
FROM FIXED_ASSETS F
WHERE
F.ORG_ID=#{orgId}

		AND F.CREATE_TIME >=#{beginTime}


		AND F.CREATE_TIME <=#{endTime}


	AND (F.ASSETS_NAME LIKE #{search} OR F.ASSETS_CODE LIKE #{search})


		AND F.SORT_ID = #{sortId}

SELECT F.ASSETS_ID AS assetsId,F.ASSETS_NAME AS assetsName,F.OWN_DEPT AS ownDept,F.BRAND AS brand,F.MODEL AS model,
F.ASSETS_CODE AS assetsCode,F.PURCHASE_PRICE AS purchasePrice,F.CREATE_TIME AS createTime,F.SORT_ID AS sortId,
(SELECT S.SORT_NAME FROM FIXED_ASSETS_SORT S WHERE S.ORG_ID=#{orgId} AND F.SORT_ID=S.SORT_ID) AS sortName,
(SELECT D.DEPT_NAME FROM UNIT_DEPT D WHERE D.ORG_ID=#{orgId} AND D.DEPT_ID=F.OWN_DEPT) AS ownDeptName,F.STATUS AS status
FROM FIXED_ASSETS F
WHERE
F.ORG_ID=#{orgId}

	AND F.CREATE_TIME >=#{beginTime}


	AND F.CREATE_TIME <=#{endTime}


	AND (F.ASSETS_NAME LIKE #{search} OR F.ASSETS_CODE LIKE #{search})


	AND F.SORT_ID = #{sortId}


	AND F.OWN_DEPT = #{ownDept}


	AND F.STATUS = #{status}

SELECT F.ASSETS_ID AS assetsId,F.ASSETS_NAME AS assetsName,F.OWN_DEPT AS ownDept,F.BRAND AS brand,F.MODEL AS model, F.ASSETS_CODE AS assetsCode,F.PURCHASE_PRICE AS purchasePrice,F.CREATE_TIME AS createTime,F.SORT_ID AS sortId, (SELECT S.SORT_NAME FROM FIXED_ASSETS_SORT S WHERE S.ORG_ID=#{orgId} AND F.SORT_ID=S.SORT_ID) AS sortName, (SELECT D.DEPT_NAME FROM UNIT_DEPT D WHERE D.ORG_ID=#{orgId} AND D.DEPT_ID=F.OWN_DEPT) AS ownDeptName,F.STATUS AS status FROM FIXED_ASSETS F WHERE F.ORG_ID=#{orgId}
AND (F.ASSETS_NAME LIKE #{search} OR F.ASSETS_CODE LIKE #{search})
AND F.SORT_ID = #{sortId}
AND F.STATUS = '0'

SELECT A.APPLY_ID AS applyId,F.ASSETS_ID AS assetsId,F.ASSETS_NAME AS assetsName,F.OWN_DEPT AS ownDept,F.BRAND AS brand,F.MODEL AS model,
F.ASSETS_CODE AS assetsCode,F.PURCHASE_PRICE AS purchasePrice,A.CREATE_TIME AS createTime,F.SORT_ID AS sortId,
(SELECT S.SORT_NAME FROM FIXED_ASSETS_SORT S WHERE S.ORG_ID=#{orgId} AND F.SORT_ID=S.SORT_ID) AS sortName,
(SELECT D.DEPT_NAME FROM UNIT_DEPT D WHERE D.ORG_ID=#{orgId} AND D.DEPT_ID=F.OWN_DEPT) AS ownDeptName,F.STATUS AS status 
FROM FIXED_ASSETS F LEFT JOIN FIXED_ASSETS_APPLY A ON A.ORG_ID=#{orgId} AND F.ASSETS_ID=A.ASSETS_ID AND F.STATUS='0'
WHERE
F.ORG_ID=#{orgId}
AND F.STATUS='0'

	AND A.CREATE_TIME >=#{beginTime}


	AND A.CREATE_TIME <=#{endTime}


	AND (F.ASSETS_NAME LIKE #{search} OR F.ASSETS_CODE LIKE #{search})


	AND F.SORT_ID = #{sortId}

	AND F.ASSETS_ID IN (SELECT ASSETS_ID FROM FIXED_ASSETS_APPROVAL A ,FIXED_ASSETS_APPLY B WHERE A.ORG_ID=#{orgId} AND A.ORG_ID=B.ORG_ID AND A.STATUS='1')
